<script>
    function copyLink(code) {
        let copyText = document.getElementById("transferText");
        copyText.innerText = 'Copied';
        try {
            // Attempt to copy using the new asynchronous Clipboard API
            navigator.clipboard.writeText(code).then(function() {
            }).catch(function(err) {
                // Fallback to the document.execCommand method for browsers that don't support Clipboard API
                document.execCommand('copy');
            });
        } catch (err) {
            // Fallback to the document.execCommand method for browsers that don't support Clipboard API
            document.execCommand('copy');
        }
        setTimeout(() => {
            copyText.innerText = 'Copy Your Transfer Code';
        }, 5000);
    }

</script>
